import { Link, useLocation } from 'react-router-dom'
import './singlepost.css'
import { useContext, useEffect, useState } from 'react';
import axios from "axios";
import { Context } from '../../context/Context';

export default function Singlepost(){
    const location = useLocation();
    const path = location.pathname.split("/")[2];
    const [post,setPost]=useState({});
    const PF = "http://localhost:3000/images/"
    const {user} = useContext(Context)
    const [title,setTitle]=useState("");
    const [desc,setDesc]=useState("");
    const [updateMode,setUpdateMode]=useState(false);



    useEffect(()=>{
        const getPost = async()=>{
            const res =await axios.get("/posts/"+path)
            setPost(res.data);
            setTitle(res.data.title);
            setDesc(res.data.desc)
        }
        getPost();
    },[path])

    const handleDelete = async()=>{
        try{
            await axios.delete(`/posts/${post._id}`,{
                data:{username:user.username}});
            window.location.replace("/");
        }
        catch(err)
        {

        }
    }
    console.log(user);
    return(
        <div className='singlePost'>
            <div className='singlePostWrapper'>
                {post.photo && (
                <img className='singlePostImg' src={PF+post.photo} alt='default'/>
                )}
                {
                    updateMode ?<input type='text' value={title} className='singlePostTitleInput' autoFocus onChange={(e)=>setDesc(e.target.value)}/>:(
                
                <h1 className='singlePostTitle'>
                    {post.title}
                    {post.username === user?.username && (
                    <div className='singlePostEdit'>
                        <i className="singlePostIcon fa-regular fa-pen-to-square" onClick={()=>setUpdateMode(true)}></i>
                        <i className="singlePostIcon fa-sharp fa-regular fa-trash" onClick={handleDelete}></i>
                    </div>
                    )}
                </h1>
                )}
                <div className='singlePostInfo'>
                    <span className='singlePostAuthor'>Author:<Link to={`/?user=${post.username}`} className='link'>
                        <b>{post.username}</b></Link></span>
                    <span className='singlePostDate'>{new Date(post.createdAt).toDateString()}</span>
                </div>
                {updateMode?(<textarea className='singlePostDescInput' value={desc} onChange={(e)=>setDesc(e.target.value)}/>):(
                <p className='singlePostDesc'>
                    {post.desc}
                </p>
                )}
            </div>
        </div>
    )
}
