import { useLocation } from 'react-router-dom'
import './singlepost.css'
import { useEffect, useState } from 'react';
import axios from "axios";

export default function Singlepost(){
    const location = useLocation();
    const path = location.pathname.split("/")[2];
    const [post,setPost]=useState({});

    useEffect(()=>{
        const getPost = async()=>{
            const res =await axios.get("/posts/"+path)
            setPost(res.data);
        }
        getPost();
    },[path])
    return(
        <div className='singlePost'>
            <div className='singlePostWrapper'>
                {post.photo && (
                <img className='singlePostImg' src='https://media.cnn.com/api/v1/images/stellar/prod/220922170938-glacier-calving.jpg?c=4x3' alt='default'/>
                )}
                <h1 className='singlePostTitle'>Lorem ipsum dolor sit
                    <div className='singlePostEdit'>
                        <i className="singlePostIcon fa-regular fa-pen-to-square"></i>
                        <i className="singlePostIcon fa-sharp fa-regular fa-trash"></i>
                    </div>
                </h1>
                <div className='singlePostInfo'>
                    <span className='singlePostAuthor'>Author: <b>Lalsiam</b></span>
                    <span className='singlePostDate'>1 hour ago</span>
                </div>
                <p className='singlePostDesc'>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
                sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
                sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                </p>
            </div>
        </div>
    )
}
